                    Wind Speed: ${data.wind_speed_10m}
                    now: ${data.is_day === 0 ? 'night' : 'day'}
                    Humidity: ${data.relative_humidity_2m}
                    Rain ${data.rain}%
                    `
                    console.log(div)

                    const currentDate = new Date();
                    const tomorrowDate = new Date(currentDate.getTime() + (24 * 60 * 60 * 1000));
                    const tomorrowDateString = tomorrowDate.toISOString().split('T')[0];
                    const tomorrowForecastIndex = data.time.indexOf(tomorrowDateString + 'T00:00');
                    const temperatureForecastTomorrow = data.temperature_2m[tomorrowForecastIndex];
                    console.log("Tomorrow's temperature forecast:", temperatureForecastTomorrow);

                    const min_grade = Math.round(temperatureForecastTomorrow) 
                    const min_gradeElement = document.getElementById('min-grade')
                    min_gradeElement.textContent = min_grade + 'Â°C'




        }

